// Modify adds a query modifier for attaching custom logic to queries.
func (wus *WorkspaceUserSelect) Modify(modifiers ...func(s *sql.Selector)) *WorkspaceUserSelect {
	wus.modifiers = append(wus.modifiers, modifiers...)
	return wus
}
